#Program to calculate score by sliding frame method
#Returns a file with first nucleotide positions of conserved frames

master_sequence = ''

upper = 95	#including this
lower = 90
frame_size = 100

out_file = open('conserved.txt', 'w')

scores = []	#all scores
potential = []	#scores above threshold

for i in range(len(master_sequence) - frame_size):
	cur_seq = master_sequence[i:i+100]
	score = cur_seq.count('1')
	scores.append(score)
	if score > lower and score <= upper:
		potential.append(i)
		out_file.write(str(i) + ',')

out_file.close()